function extractEth0Config(content) {
    const eth0Block = content.match(/iface eth0 inet static([\s\S]*?)metric \d+/);
    if (!eth0Block) return null;

    const block = eth0Block[1];

    const address = block.match(/address\s+([^\s]+)/)?.[1];
    const netmask = block.match(/netmask\s+([^\s]+)/)?.[1];
    const gateway = block.match(/gateway\s+([^\s]+)/)?.[1];
    const dns = block.match(/dns-nameservers\s+([^\n]+)/)?.[1]?.split(/\s+/);

    return {
        address,
        netmask,
        gateway,
        primary_dns: dns?.[0] || null,
        second_dns: dns?.[1] || null
    };
}
